import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./RetailerOrders.css";

const RetailerOrders = () => {
  const navigate = useNavigate();

  const [orders, setOrders] = useState([]);
  const [retailer, setRetailer] = useState(null);
  const [loading, setLoading] = useState(true);

  // Load retailer + orders
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("retailer"));
    if (!stored) {
      navigate("/retailer/login");
      return;
    }
    setRetailer(stored);
    loadOrders(stored.id);
  }, [navigate]);

  const loadOrders = async (retailerId) => {
    try {
      const res = await orderAPI.getRetailerOrders(retailerId);
      setOrders(res.data || []);
    } catch (err) {
      console.error("Error loading orders:", err);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "‚Äî";
    const d = new Date(dateString);
    if (isNaN(d.getTime())) return "‚Äî";
    return d.toLocaleString("en-IN", {
      dateStyle: "medium",
      timeStyle: "short",
    });
  };

  // Badge UI
  const getStatusBadge = (paymentStatus) => {
    switch (paymentStatus) {
      case "PAID":
        return <span className="paid">PAID</span>;
      case "PENDING":
      default:
        return <span className="pending">PENDING</span>;
    }
  };

  return (
    <div className="orders-page">

      {/* HEADER */}
      <div className="orders-header">
        <button className="back-btn" onClick={() => navigate("/retailer/dashboard")}>
          ‚Üê Back to Dashboard
        </button>

        <h1 className="page-title">üì¶ My Orders</h1>

        <p className="welcome-text">
          Welcome back, <b>{retailer?.storeName || retailer?.name}</b>
        </p>
      </div>

      {/* TABLE SECTION */}
      <div className="orders-container">
        {loading ? (
          <div className="loading">Loading orders...</div>
        ) : (
          <table className="orders-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Date</th>
                <th>Total</th>
                <th>Payment Method</th>
                <th>Payment Status</th>
                <th>View</th>
              </tr>
            </thead>

            <tbody>
              {orders.length === 0 ? (
                <tr>
                  <td colSpan="6" className="no-orders">
                    No orders found üõí
                  </td>
                </tr>
              ) : (
                orders.map((o, index) => (
                  <tr
                    key={o.id}
                    className="order-row"
                    style={{ animationDelay: `${index * 0.07}s` }}
                  >
                    <td>{o.id}</td>
                    <td>{formatDate(o.createdAt)}</td>
                    <td>‚Çπ{(o.totalAmount || 0).toFixed(2)}</td>
                    <td>{o.paymentMethod || "‚Äî"}</td>
                    <td>{getStatusBadge(o.paymentStatus)}</td>

                    <td>
                      <button
                        className="view-btn"
                        onClick={() =>
                          navigate(`/order-details/${o.id}`, {
                            state: { order: o },
                          })
                        }
                      >
                        üîç View
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default RetailerOrders;
