import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { transporterAPI } from "../services/api";

const TransportLogin = () => {
  const [form, setForm] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      console.log("üöÄ Logging in transporter:", form);
      const res = await transporterAPI.login(form);
      const transporter = res.data;

      if (transporter) {
        // ‚úÖ Store all authentication info
        localStorage.setItem("isAuthenticated", "true");
        localStorage.setItem("userType", "transporter");
        localStorage.setItem("user", JSON.stringify(transporter));
        localStorage.setItem("transporter", JSON.stringify(transporter));

        alert(`‚úÖ Welcome, ${transporter.name || "Transporter"}!`);
        navigate("/transport/dashboard"); // ‚úÖ Redirect
      } else {
        alert("‚ùå Invalid response from server.");
      }
    } catch (err) {
      console.error("‚ùå Login failed:", err);
      alert("‚ùå Invalid email or password.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <h1>üöö Transporter Login</h1>
      <form onSubmit={handleSubmit}>
        <input
          name="email"
          type="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "‚è≥ Logging in..." : "Login"}
        </button>
      </form>
      <p>
        New here? <Link to="/transport/signup">Signup</Link>
      </p>
    </div>
  );
};

export default TransportLogin;
