import React, { useState } from "react";
import { transporterAuthAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";

const TransportLogin = () => {
  const [form, setForm] = useState({ email: "", password: "" });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await transporterAuthAPI.login(form);
      const transporter = res.data;

      // ‚úÖ Save only necessary info (don't store password)
      localStorage.setItem("transporter", JSON.stringify(transporter));
      localStorage.setItem("isAuthenticated", "true");
      localStorage.setItem("userType", "transporter");

      alert(`‚úÖ Welcome ${transporter.name || "Transporter"}!`);
      navigate("/transport/dashboard");
    } catch (err) {
      console.error("Login failed:", err);
      alert(err.response?.data?.message || "‚ùå Invalid credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-bg">
      <div className="auth-container">
        <div className="auth-card">
          <div className="auth-header">
            <h1>üöõ Transporter Login</h1>
            <p>Access your dashboard and manage deliveries.</p>
          </div>

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="input-group">
              <label>Email</label>
              <input
                name="email"
                type="email"
                placeholder="Enter your email"
                value={form.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <label>Password</label>
              <input
                name="password"
                type="password"
                placeholder="Enter your password"
                value={form.password}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit" className="auth-btn" disabled={loading}>
              {loading ? "‚è≥ Logging in..." : "üîë Login Now"}
            </button>
          </form>

          <p className="auth-footer">
            Don‚Äôt have an account?{" "}
            <Link to="/transport/signup" className="auth-link">
              Register here
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default TransportLogin;
