import React, { useState } from "react";
import { transporterAuthAPI } from "../services/api";
import { useNavigate, Link } from "react-router-dom";

const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await transporterAuthAPI.signup(form);
      alert("‚úÖ Registration successful! Redirecting to login...");
      setForm({ name: "", email: "", password: "", phone: "", vehicleNo: "" });
      navigate("/transport/login");
    } catch (err) {
      console.error("Signup failed:", err);
      alert(
        err.response?.data?.message ||
          "‚ùå Signup failed. Please try again later."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-bg">
      <div className="auth-container">
        <div className="auth-card">
          <div className="auth-header">
            <h1>üöõ Transporter Registration</h1>
            <p>Join our trusted logistics network today.</p>
          </div>

          <form onSubmit={handleSubmit} className="auth-form">
            {["name", "email", "password", "phone", "vehicleNo"].map((field) => (
              <div className="input-group" key={field}>
                <label>
                  {field === "vehicleNo"
                    ? "Vehicle Number"
                    : field.charAt(0).toUpperCase() + field.slice(1)}
                </label>
                <input
                  name={field}
                  type={field === "password" ? "password" : "text"}
                  placeholder={`Enter your ${field}`}
                  value={form[field]}
                  onChange={handleChange}
                  required
                />
              </div>
            ))}

            <button type="submit" className="auth-btn" disabled={loading}>
              {loading ? "‚è≥ Registering..." : "üöö Register Now"}
            </button>
          </form>

          <p className="auth-footer">
            Already have an account?{" "}
            <Link to="/transport/login" className="auth-link">
              Login here
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default TransportSignup;
