import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ✅ Fetch products from API
  const fetchProducts = async () => {
    setLoading(true);
    try {
      const response = await productAPI.getFarmerProducts();
      console.log("✅ Products fetched:", response.data);
      setProducts(response.data);
      setError("");
    } catch (err) {
      console.error("❌ Fetch products error:", err);

      // Safe readable error message
      let message;
      if (err.response?.data) {
        message =
          typeof err.response.data === "string"
            ? err.response.data
            : JSON.stringify(err.response.data, null, 2);
      } else {
        message = err.message || "Unknown error occurred";
      }

      setError(`Error fetching products:\n${message}`);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [refreshKey]); // ✅ Refresh list when new product added

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith("http")) return imagePath;
    if (imagePath.startsWith("/uploads/")) {
      return `http://localhost:8080${imagePath}`;
    }
    return `http://localhost:8080/uploads/${imagePath}`;
  };

  if (loading) return <div className="loading">Loading products...</div>;

  if (error)
    return (
      <div className="error-message">
        <pre style={{ whiteSpace: "pre-wrap", color: "red" }}>{error}</pre>
      </div>
    );

  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>View Product List</h2>
      </div>

      {products.length === 0 ? (
        <div className="no-products">
          <p>No products added yet.</p>
          <p>Add your first product using the form!</p>
        </div>
      ) : (
        <div className="products-grid">
          {products.map((product) => {
            const imageUrl = getImageUrl(product.imagePath);
            return (
              <div key={product.id} className="product-card">
                <div className="product-image-container">
                  {imageUrl ? (
                    <img
                      src={imageUrl}
                      alt={product.name}
                      className="product-image"
                      onError={(e) => {
                        console.error(`Failed to load image: ${imageUrl}`);
                        e.target.style.display = "none";
                        e.target.nextSibling.style.display = "flex";
                      }}
                    />
                  ) : null}
                  <div
                    className={`image-placeholder ${imageUrl ? "hidden" : ""}`}
                  >
                    <span>No Photo</span>
                  </div>
                </div>

                <div className="product-info">
                  <h3 className="product-name">{product.name}</h3>
                  <div className="product-details">
                    <div className="detail-row">
                      <span className="detail-label">Category:</span>
                      <span className="detail-value">{product.category}</span>
                    </div>
                    <div className="detail-row">
                      <span className="detail-label">Price:</span>
                      <span className="detail-value price">
                        ₹{product.price}
                      </span>
                    </div>
                    <div className="detail-row">
                      <span className="detail-label">Batch No:</span>
                      <span className="detail-value">
                        {product.batchNumber}
                      </span>
                    </div>
                    <div className="detail-row">
                      <span className="detail-label">Production:</span>
                      <span className="detail-value">
                        {new Date(product.productionDate).toLocaleDateString()}
                      </span>
                    </div>
                    {product.description && (
                      <div className="detail-row description-row">
                        <span className="detail-label">Description:</span>
                        <span className="detail-value description">
                          {product.description}
                        </span>
                      </div>
                    )}
                    <div className="detail-row">
                      <span className="detail-label">Added:</span>
                      <span className="detail-value">
                        {new Date(product.createdAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ProductList;
