import React, { useEffect, useState } from "react";
import { cartAPI } from "../services/api";
import { useNavigate, Link } from "react-router-dom";
import "./RetailerCart.css";

const RetailerCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [retailer, setRetailer] = useState(null);
  const navigate = useNavigate();

  // ‚úÖ Load retailer info
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (storedRetailer) {
      setRetailer(storedRetailer);
      fetchCart(storedRetailer.id);
    } else {
      navigate("/retailer/login");
    }
  }, [navigate]);

  // ‚úÖ Fetch cart items
  const fetchCart = async (retailerId) => {
    try {
      const response = await cartAPI.getCart(retailerId);
      setCartItems(response.data);
    } catch (err) {
      console.error("Error loading cart:", err);
    }
  };

  // ‚úÖ Update quantity
  const handleQuantityChange = async (itemId, newQty) => {
    try {
      await cartAPI.updateQuantity(itemId, { quantity: newQty });
      setCartItems((prev) =>
        prev.map((item) =>
          item.id === itemId ? { ...item, quantity: newQty } : item
        )
      );
    } catch (err) {
      console.error("Error updating quantity:", err);
    }
  };

  // ‚úÖ Remove item
  const handleRemove = async (itemId) => {
    await cartAPI.removeItem(itemId);
    setCartItems(cartItems.filter((item) => item.id !== itemId));
  };

  // ‚úÖ Clear cart
  const handleClearCart = async () => {
    if (retailer) {
      await cartAPI.clearCart(retailer.id);
      setCartItems([]);
    }
  };

  const totalPrice = cartItems.reduce(
    (sum, item) => sum + item.product.price * item.quantity,
    0
  );

  return (
    <div className="cart-container">
      <header className="cart-header">
        <div className="logo">üõí Product Tracking</div>
        <nav>
          <Link to="/retailer/dashboard" className="nav-link">
            Dashboard
          </Link>
          <Link to="/retailer/products" className="nav-link">
            Products
          </Link>
          <Link to="/retailer/view-farmers" className="nav-link">
            View Farmers
          </Link>
        </nav>
      </header>

      <h2>Your Shopping Cart</h2>

      {cartItems.length === 0 ? (
        <p className="empty-cart">Your cart is empty.</p>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Image</th>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>
                    <img
                      src={`http://localhost:8080/uploads/${item.product.imagePath}`}
                      alt={item.product.name}
                      className="cart-img"
                    />
                  </td>
                  <td>{item.product.name}</td>
                  <td>‚Çπ{item.product.price}</td>
                  <td>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) =>
                        handleQuantityChange(item.id, e.target.value)
                      }
                    />
                  </td>
                  <td>‚Çπ{(item.product.price * item.quantity).toFixed(2)}</td>
                  <td>
                    <button
                      onClick={() => handleRemove(item.id)}
                      className="remove-btn"
                    >
                      ‚ùå
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="cart-summary">
            <h3>Total: ‚Çπ{totalPrice.toFixed(2)}</h3>
            <button onClick={handleClearCart} className="clear-btn">
              üóëÔ∏è Clear Cart
            </button>
            <button
              onClick={() => alert("Checkout coming soon!")}
              className="checkout-btn"
            >
              ‚úÖ Checkout
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default RetailerCart;
