import axios from 'axios';

const BASE_URL = 'http://localhost:8080/api';

export const productAPI = {
  // Farmer Product APIs
  getFarmerProducts: () => axios.get(`${BASE_URL}/farmer/products`),
  addProduct: (formData) =>
    axios.post(`${BASE_URL}/farmer/products`, formData, {
      headers: { 'Content-Type': 'multipart/form-data' },
    }),

  // Retailer Product APIs
  getRetailerProducts: () => axios.get(`${BASE_URL}/retailer/products`),
};

export const authAPI = {
  farmerLogin: (data) => axios.post(`${BASE_URL}/auth/login`, data),
  farmerSignup: (data) => axios.post(`${BASE_URL}/auth/signup`, data),

  retailerLogin: (data) => axios.post(`${BASE_URL}/retailer/auth/login`, data),
  retailerSignup: (data) => axios.post(`${BASE_URL}/retailer/auth/signup`, data),

  transportLogin: (data) => axios.post(`${BASE_URL}/transport/auth/login`, data),
  transportSignup: (data) => axios.post(`${BASE_URL}/transport/auth/signup`, data),
};
