import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import TransportShipments from './TransportShipments';
import TransportProfile from './TransportProfile';

const TransportDashboard = ({ transport, logout }) => {
  const [activeTab, setActiveTab] = useState('shipments');

  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <span style={{ color: 'white' }}>Welcome, {transport?.companyName}</span>
          <button onClick={logout} className="logout-btn">LOGOUT</button>
        </div>
      </nav>

      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Transport Dashboard</h1>
          <p>Welcome back, {transport?.ownerName} | {transport?.companyName} | Vehicle: {transport?.vehicleNumber}</p>
        </div>

        <div className="transport-tabs">
          <button 
            className={`tab-btn ${activeTab === 'shipments' ? 'active' : ''}`}
            onClick={() => setActiveTab('shipments')}
          >
            My Shipments
          </button>
          <button 
            className={`tab-btn ${activeTab === 'available' ? 'active' : ''}`}
            onClick={() => setActiveTab('available')}
          >
            Available Shipments
          </button>
          <button 
            className={`tab-btn ${activeTab === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveTab('profile')}
          >
            Company Profile
          </button>
        </div>

        <div className="dashboard-content">
          {activeTab === 'shipments' && <TransportShipments transport={transport} />}
          {activeTab === 'available' && (
            <div className="tab-content">
              <h3>Available Shipments</h3>
              <p>Available shipments listing coming soon...</p>
            </div>
          )}
          {activeTab === 'profile' && <TransportProfile transport={transport} />}
        </div>
      </div>
    </div>
  );
};

export default TransportDashboard;
