import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./RetailerOrders.css";

const RetailerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const retailer = JSON.parse(localStorage.getItem("retailer"));
  const retailerId = retailer?.id;

  const navigate = useNavigate();

  useEffect(() => {
    if (retailerId) fetchOrders(retailerId);
  }, [retailerId]);

  const fetchOrders = async (id) => {
    try {
      const res = await orderAPI.getRetailerOrders(id);
      setOrders(Array.isArray(res.data) ? res.data : []);
    } catch (err) {
      console.error("Error fetching retailer orders:", err);
      setOrders([]);
    }
  };

  // --------------------------
  // View Order Details
  // --------------------------
  const viewOrderDetails = async (orderId) => {
    try {
      const res = await orderAPI.getOrderDetails(orderId);
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      console.error("Error loading order details:", err);
    }
  };

  const closeModal = () => {
    setSelectedOrder(null);
    setShowModal(false);
  };

  // --------------------------
  // Confirm Delivery
  // --------------------------
  const confirmDelivery = async (orderId) => {
    if (!window.confirm("‚úî Confirm you received this order?")) return;

    try {
      await orderAPI.confirmReceipt(orderId);
      alert("Delivery confirmed!");

      // Update local state
      setOrders((prev) =>
        prev.map((o) =>
          o.id === orderId ? { ...o, status: "DELIVERED" } : o
        )
      );
    } catch (err) {
      console.error("Confirm receipt failed:", err);
      alert("‚ùå Failed to confirm delivery");
    }
  };

  const goBack = () => navigate("/retailer/dashboard");

  if (!retailerId)
    return <h3>No retailer logged in. Please login again.</h3>;

  return (
    <div className="orders-page">
      <div className="page-header">
        <button className="back-btn" onClick={goBack}>
          ‚Üê Back
        </button>
        <h1>üì¶ My Orders</h1>
      </div>

      {orders.length === 0 ? (
        <p className="empty-text">No orders found.</p>
      ) : (
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Total</th>
              <th>Payment</th>
              <th>Status</th>
              <th>Details</th>
              <th>Confirm</th>
            </tr>
          </thead>

          <tbody>
            {orders.map((o) => (
              <tr key={o.id}>
                <td>{o.id}</td>
                <td>{new Date(o.createdAt).toLocaleString()}</td>
                <td>‚Çπ{o.totalAmount}</td>
                <td>{o.paymentMethod}</td>

                <td className={`status ${o.status.toLowerCase()}`}>
                  {o.status}
                </td>

                <td>
                  <button className="view-btn" onClick={() => viewOrderDetails(o.id)}>
                    üîç View
                  </button>
                </td>

                <td>
                  {o.status !== "DELIVERED" ? (
                    <button
                      className="confirm-btn"
                      onClick={() => confirmDelivery(o.id)}
                    >
                      ‚úî Confirm
                    </button>
                  ) : (
                    <span className="delivered-label">Delivered</span>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* --------------------------
          MODAL ‚Äî ORDER DETAILS
      --------------------------- */}
      {showModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>Order #{selectedOrder.id}</h2>

            <p>
              <b>Date:</b>{" "}
              {new Date(selectedOrder.createdAt).toLocaleString()}
            </p>

            <p>
              <b>Total Amount:</b> ‚Çπ{selectedOrder.totalAmount}
            </p>

            <p>
              <b>Payment Method:</b> {selectedOrder.paymentMethod}
            </p>

            <p>
              <b>Status:</b> {selectedOrder.status}
            </p>

            <h3>üõí Items</h3>

            <table className="details-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Qty</th>
                  <th>Rate</th>
                  <th>Total</th>
                </tr>
              </thead>

              <tbody>
                {selectedOrder.items.map((it, idx) => (
                  <tr key={idx}>
                    <td>{it.productName}</td>
                    <td>{it.quantity}</td>
                    <td>‚Çπ{it.price}</td>
                    <td>‚Çπ{it.total}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <button className="close-btn" onClick={closeModal}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RetailerOrders;
