import React, { useState, useEffect } from 'react';
import { retailerProductAPI } from '../services/api';
import './RetailerProductList.css';

const RetailerProductList = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await retailerProductAPI.getAllProducts();
        setProducts(response.data);
      } catch (err) {
        setError('Failed to load products');
        console.error(err);
      }
    };
    fetchProducts();
  }, []);

  if (error) return <div className="error">{error}</div>;
  if (products.length === 0) return <div>No products available.</div>;

  return (
    <div className="product-list">
      {products.map((product) => (
        <div className="product-card" key={product.id}>
          <img
            src={`http://localhost:8080${product.imagePath}`}
            alt={product.name}
            className="product-image"
          />
          <h3>{product.name}</h3>
          <p><strong>Category:</strong> {product.category}</p>
          <p><strong>Price:</strong> â‚¹{product.price}</p>
          <p><strong>Farmer:</strong> {product.farmerName}</p>
          <button onClick={() => addToCart(product)} className="add-btn">
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
};

export default RetailerProductList;
