import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import FarmerSignup from "./components/FarmerSignup";
import FarmerLogin from "./components/FarmerLogin";
import FarmerDashboard from "./components/FarmerDashboard";
import AddProduct from "./components/AddProduct";
import ProductList from "./components/ProductList";

const App = () => {
  const [user, setUser] = useState(null);

  const handleLogin = (userData) => setUser(userData);
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("farmer");
  };

  return (
    <Router>
      <Routes>
        {/* Authentication */}
        <Route path="/farmer/signup" element={<FarmerSignup />} />
        <Route path="/farmer/login" element={<FarmerLogin login={handleLogin} />} />

        {/* Farmer Dashboard */}
        <Route
          path="/farmer/dashboard"
          element={<FarmerDashboard user={user} logout={handleLogout} />}
        />

        {/* Product Management */}
        <Route path="/farmer/add-product" element={<AddProduct />} />
        <Route path="/farmer/products" element={<ProductList />} />
      </Routes>
    </Router>
  );
};

export default App;
