// ==================================================
// src/services/api.js
// Centralized API configuration for Farmer & Retailer
// ==================================================

import axios from "axios";

// ==================================================
//                 BASE AXIOS INSTANCE
// ==================================================

const BASE_URL = "http://localhost:8080/api";

const api = axios.create({
  baseURL: BASE_URL,
  withCredentials: true, // Required for Spring Security sessions/cookies
});

// ==================================================
//               AUTH INTERCEPTOR
// ==================================================
api.interceptors.request.use((config) => {
  try {
    const farmer = JSON.parse(localStorage.getItem("farmer"));
    const retailer = JSON.parse(localStorage.getItem("retailer"));
    const user = farmer || retailer;

    if (user && user.email && user.password) {
      const token = btoa(`${user.email}:${user.password}`);
      config.headers.Authorization = `Basic ${token}`;
    }
  } catch (err) {
    console.warn("Error in axios interceptor:", err);
  }
  return config;
});

// ==================================================
//                   AUTH APIS
// ==================================================
export const authAPI = {
  farmerSignup: (data) => api.post("/auth/farmer/register", data),
  farmerLogin: (data) => api.post("/auth/farmer/login", data),
};

export const retailerAuthAPI = {
  signup: (data) => api.post("/retailer/auth/signup", data),
  login: (data) => api.post("/retailer/auth/login", data),
};

// ==================================================
//                  FARMER APIS
// ==================================================
export const farmerAPI = {
  getAllFarmers: () => api.get("/farmers"),
  getFarmerById: (farmerId) => api.get(`/farmers/${farmerId}`), // ✅ fixed duplicate
};

// ==================================================
//                 PRODUCT APIS
// ==================================================
export const productAPI = {
  addProduct: (formData) =>
    api.post("/farmer/products", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    }),
  getFarmerProducts: () => api.get("/farmer/products"),
  getProductsByFarmer: (farmerId) => api.get(`/farmer/${farmerId}/products`),
  getAllProducts: () => api.get("/products"),
};

// ==================================================
//                 RETAILER APIS
// ==================================================
export const retailerAPI = {
  // ✅ Unified with same api instance
  getAllFarmersWithProducts: () => api.get("/retailers/farmers-with-products"),

  // ✅ Used in FarmerProducts.js
  getFarmerById: (farmerId) => api.get(`/farmers/${farmerId}`),
};

// ==================================================
//                   CART APIS
// ==================================================
export const cartAPI = {
  addToCart: (data) => api.post("/cart/add", data),
  getCart: (retailerId) => api.get(`/cart/${retailerId}`),
  updateQuantity: (itemId, payload) =>
    api.put(`/cart/update/${itemId}`, payload),
  removeItem: (itemId) => api.delete(`/cart/remove/${itemId}`),
  clearCart: (retailerId) => api.delete(`/cart/clear/${retailerId}`),
};

// ==================================================
//                   ORDER APIS
// ==================================================
export const orderAPI = {
  placeOrder: (retailerId, paymentMethod) =>
    api.post(`/orders/place/${retailerId}?paymentMethod=${paymentMethod}`),
  getOrders: (retailerId) => api.get(`/orders/${retailerId}`),
  getOrderDetails: (orderId) => api.get(`/orders/details/${orderId}`),
};

// ==================================================
//                   DEFAULT EXPORT
// ==================================================
export default api;
