import React, { useState, useEffect } from 'react';
import { retailerProductAPI } from '../services/api';
import './RetailerProductList.css';

const RetailerProductList = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  useEffect(() => {
    filterProducts();
  }, [products, searchTerm, selectedCategory]);

  const fetchProducts = async () => {
    try {
      const response = await retailerProductAPI.getAllProducts();
      setProducts(response.data);
      setError('');
    } catch (error) {
      setError('Error fetching products: ' + (error.response?.data || error.message));
      console.error('Fetch products error:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterProducts = () => {
    let filtered = products;

    if (searchTerm) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        product.category.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (selectedCategory) {
      filtered = filtered.filter(product =>
        product.category.toLowerCase() === selectedCategory.toLowerCase()
      );
    }

    setFilteredProducts(filtered);
  };

  const getCategories = () => {
    const categories = [...new Set(products.map(product => product.category))];
    return categories;
  };

  const refreshProducts = () => {
    setLoading(true);
    fetchProducts();
  };

  if (loading) return <div className="loading">Loading products...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="retailer-product-list">
      <div className="retailer-product-header">
        <h2>Available Products</h2>
        <button onClick={refreshProducts} className="refresh-btn">
          Refresh
        </button>
      </div>

      <div className="filters">
        <div className="search-box">
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
        <div className="category-filter">
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="category-select"
          >
            <option value="">All Categories</option>
            {getCategories().map(category => (
              <option key={category} value={category}>{category}</option>
            ))}
          </select>
        </div>
      </div>

      <div className="products-stats">
        <p>Showing {filteredProducts.length} of {products.length} products</p>
      </div>

      {filteredProducts.length === 0 ? (
        <div className="no-products">
          <p>No products found matching your criteria.</p>
        </div>
      ) : (
        <div className="products-grid">
          {filteredProducts.map(product => (
            <div key={product.id} className="product-card">
              <div className="product-image-container">
                {product.imageUrl ? (
                  <img 
                    src={`http://localhost:8080/uploads/${product.imageUrl}`} 
                    alt={product.name}
                    className="product-image"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className={`image-placeholder ${product.imageUrl ? 'hidden' : ''}`}>
                  <span>No Image</span>
                </div>
              </div>

              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <div className="product-details">
                  <div className="detail-row">
                    <span className="detail-label">Category:</span>
                    <span className="detail-value">{product.category}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Price:</span>
                    <span className="detail-value price">${product.price}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Batch No:</span>
                    <span className="detail-value">{product.batchNumber}</span>
                  </div>
                  <div className="detail-row">
                    <span className="detail-label">Production:</span>
                    <span className="detail-value">
                      {new Date(product.productionDate).toLocaleDateString()}
                    </span>
                  </div>
                  {product.description && (
                    <div className="detail-row description-row">
                      <span className="detail-label">Description:</span>
                      <span className="detail-value description">{product.description}</span>
                    </div>
                  )}
                  <div className="detail-row">
                    <span className="detail-label">Farmer ID:</span>
                    <span className="detail-value">FARM{product.farmerId}</span>
                  </div>
                </div>
                <div className="product-actions">
                  <button className="contact-btn">Contact Farmer</button>
                  <button className="order-btn">Place Order</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default RetailerProductList;
