import React, { useState } from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import RetailerProductList from './RetailerProductList';
import RetailerCart from './RetailerCart';
import './RetailerDashboard.css';

const RetailerDashboard = ({ user, logout }) => {
  const [cartItems, setCartItems] = useState([]);
  const location = useLocation();

  // Determine active tab
  const getActiveTab = () => {
    if (location.pathname.includes('/retailer/products')) return 'products';
    if (location.pathname.includes('/retailer/cart')) return 'cart';
    return 'dashboard';
  };

  const activeTab = getActiveTab();

  // Handle Add to Cart
  const handleAddToCart = (product) => {
    const exists = cartItems.find((item) => item.id === product.id);
    if (!exists) {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
      alert(`${product.name} added to cart!`);
    } else {
      alert('Product already in cart');
    }
  };

  // Handle Remove from Cart
  const handleRemoveFromCart = (id) => {
    setCartItems(cartItems.filter((item) => item.id !== id));
  };

  // Handle Place Order
  const handlePlaceOrder = () => {
    alert('Order placed successfully!');
    setCartItems([]); // Clear cart
  };

  return (
    <div>
      {/* Navbar */}
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <span style={{ color: 'white' }}>Welcome, {user?.storeName}</span>
          <button onClick={logout} className="logout-btn">LOGOUT</button>
        </div>
      </nav>

      {/* Dashboard */}
      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Retailer Dashboard</h1>
          <p>Welcome back, {user?.ownerName} | {user?.storeName}</p>
        </div>

        {/* Tabs */}
        <div className="retailer-tabs">
          <Link
            to="/retailer/dashboard"
            className={`tab-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
          >
            Dashboard
          </Link>
          <Link
            to="/retailer/products"
            className={`tab-btn ${activeTab === 'products' ? 'active' : ''}`}
          >
            Browse Products
          </Link>
          <Link
            to="/retailer/cart"
            className={`tab-btn ${activeTab === 'cart' ? 'active' : ''}`}
          >
            Cart ({cartItems.length})
          </Link>
        </div>

        {/* Routes */}
        <div className="dashboard-content">
          <Routes>
            <Route path="/dashboard" element={<DashboardHome user={user} />} />
            <Route
              path="/products"
              element={<RetailerProductList addToCart={handleAddToCart} />}
            />
            <Route
              path="/cart"
              element={
                <RetailerCart
                  cartItems={cartItems}
                  removeFromCart={handleRemoveFromCart}
                  placeOrder={handlePlaceOrder}
                />
              }
            />
          </Routes>
        </div>
      </div>
    </div>
  );
};

// âœ… Dashboard Home
const DashboardHome = ({ user }) => (
  <div className="dashboard-home">
    <div className="welcome-section">
      <h2>Welcome to Your Retail Dashboard</h2>
      <p>Manage your inventory and source products from farmers</p>
    </div>

    <div className="action-cards">
      <Link to="/retailer/products" className="action-card">
        <div className="card-icon">ðŸ›’</div>
        <h3>Browse Products</h3>
        <p>Explore products from local farmers</p>
      </Link>
      <Link to="/retailer/cart" className="action-card">
        <div className="card-icon">ðŸ§¾</div>
        <h3>View Cart</h3>
        <p>Check your selected products and place orders</p>
      </Link>
    </div>

    <div className="store-profile">
      <h3>Store Information</h3>
      <div className="profile-info">
        <p><strong>Store Name:</strong> {user?.storeName}</p>
        <p><strong>Owner:</strong> {user?.ownerName}</p>
        <p><strong>Email:</strong> {user?.email}</p>
        <p><strong>Phone:</strong> {user?.phone}</p>
        <p><strong>Address:</strong> {user?.address}</p>
        <p><strong>Business License:</strong> {user?.businessLicense}</p>
      </div>
    </div>
  </div>
);

export default RetailerDashboard;
