import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import "./TransportAuth.css"; // üëà Styling file (added below)

const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("http://localhost:8080/api/transporters/signup", form);
      alert("‚úÖ Registration successful! Redirecting to login...");
      navigate("/transport/login");
    } catch (err) {
      console.error("Signup failed:", err);
      alert("‚ùå Signup failed. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-bg">
      <div className="auth-card">
        <div className="auth-header">
          <h1>üöõ Transporter Registration</h1>
          <p>Join our trusted logistics network today.</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="input-group">
            <label>Full Name</label>
            <input
              name="name"
              type="text"
              placeholder="Enter your name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <label>Email Address</label>
            <input
              name="email"
              type="email"
              placeholder="Enter your email"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <label>Password</label>
            <input
              name="password"
              type="password"
              placeholder="Create a password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <label>Phone Number</label>
            <input
              name="phone"
              type="text"
              placeholder="Enter contact number"
              value={form.phone}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <label>Vehicle Number</label>
            <input
              name="vehicleNo"
              type="text"
              placeholder="e.g., MH12AB1234"
              value={form.vehicleNo}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="auth-btn" disabled={loading}>
            {loading ? "‚è≥ Registering..." : "üöö Register Now"}
          </button>
        </form>

        <p className="auth-footer">
          Already have an account?{" "}
          <Link to="/transport/login" className="auth-link">
            Login here
          </Link>
        </p>
      </div>
    </div>
  );
};

export default TransportSignup;
