// ==================================================
// src/services/api.js
// Updated for Spring Security Session Authentication
// ==================================================

import axios from "axios";

const BASE_URL = "http://localhost:8080/api";

const api = axios.create({
  baseURL: BASE_URL,
  withCredentials: true, // ✅ CRITICAL: This sends cookies/session with requests
});

// ==================================================
//                REQUEST INTERCEPTOR
// ==================================================
api.interceptors.request.use((config) => {
  config.headers.Accept = "application/json";

  // If FormData → do NOT set content-type
  if (!(config.data instanceof FormData)) {
    config.headers["Content-Type"] = "application/json";
  }

  return config;
});

// ==================================================
//                RESPONSE INTERCEPTOR
// ==================================================
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Redirect to login on 401
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// ==================================================
//                    AUTH APIS
// ==================================================
export const authAPI = {
  farmerSignup: (data) => api.post("/auth/farmer/register", data),
  farmerLogin: (data) => api.post("/auth/farmer/login", data),

  // Add logout endpoint
  farmerLogout: () => api.post("/auth/farmer/logout"),
};

// ==================================================
//                    RETAILER APIS
// ==================================================
export const retailerAPI = {
  signup: (data) => api.post("/retailer/auth/signup", data),
  login: (data) => api.post("/retailer/auth/login", data),
  logout: () => api.post("/retailer/auth/logout"),
  getAllFarmersWithProducts: () => api.get("/farmers/with-products"),
  getFarmerById: (farmerId) => api.get(`/farmers/${farmerId}`),
};

// ==================================================
//                    TRANSPORTER APIS
// ==================================================
export const transporterAPI = {
  signup: (data) => api.post("/transporters/signup", data),
  login: (data) => api.post("/transporters/login", data),
  logout: () => api.post("/transporters/logout"),
  getAll: () => api.get("/transporters"),
};

// ==================================================
//              AUTH UTILITY FUNCTIONS
// ==================================================
export const authUtils = {
  // Check if user is likely authenticated (client-side check)
  isAuthenticated: () => {
    // You can check for any stored user info or just rely on API responses
    return localStorage.getItem('userInfo') !== null;
  },

  // Store user info after login
  setUserInfo: (userData) => {
    localStorage.setItem('userInfo', JSON.stringify(userData));
  },

  // Get user info
  getUserInfo: () => {
    const userInfo = localStorage.getItem('userInfo');
    return userInfo ? JSON.parse(userInfo) : null;
  },

  // Logout function
  logout: () => {
    localStorage.removeItem('userInfo');
    // Call appropriate logout endpoint based on user type
    const userInfo = authUtils.getUserInfo();
    if (userInfo?.type === 'farmer') {
      authAPI.farmerLogout();
    } else if (userInfo?.type === 'retailer') {
      retailerAPI.logout();
    } else if (userInfo?.type === 'transporter') {
      transporterAPI.logout();
    }
    window.location.href = '/login';
  }
};

// ... rest of your existing API exports remain the same
export const farmerAPI = {
  getAllFarmers: () => api.get("/farmers"),
  getFarmerById: (id) => api.get(`/farmers/${id}`),
};

export const productAPI = {
  addProduct: (formData) =>
    api.post("/products/add", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    }),

  getMyProducts: () => api.get("/products/my"),
  getAllProducts: () => api.get("/products"),
  getProductsByFarmerId: (farmerId) => api.get(`/products/farmer/${farmerId}`),
};

export const cartAPI = {
  addToCart: (retailerId, productId, quantity) =>
    api.post("/cart/add", { retailerId, productId, quantity }),
  getCart: (retailerId) => api.get(`/cart/${retailerId}`),
  updateQuantity: (itemId, payload) => api.put(`/cart/update/${itemId}`, payload),
  removeItem: (itemId) => api.delete(`/cart/remove/${itemId}`),
  clearCart: (retailerId) => api.delete(`/cart/clear/${retailerId}`),
};

// ==================================================
//                    ORDER APIS (UPDATED)
// ==================================================
export const orderAPI = {
  // Retailer places order
  placeOrder: (retailerId, paymentMethod = "Online") =>
    api.post(`/orders/place/${retailerId}?paymentMethod=${paymentMethod}`),

  // Get retailer's orders
  getRetailerOrders: (retailerId) => api.get(`/orders/${retailerId}`),

  // Get retailer's delivered orders (for confirmation)
  getRetailerDeliveredOrders: (retailerId) =>
    api.get(`/orders/retailer/${retailerId}/delivered`),

  // Retailer confirms receipt
  confirmReceipt: (orderId) =>
    api.put(`/orders/${orderId}/confirm-receipt`),

  // Get farmer's orders
  getFarmerOrders: (farmerId) => api.get(`/orders/farmer/${farmerId}`),

  // Get farmer's pending orders
  getFarmerPendingOrders: (farmerId) =>
    api.get(`/orders/farmer/${farmerId}/pending`),

  // Get order details
  getOrderDetails: (orderId) => api.get(`/orders/details/${orderId}`),
};




export default api;
