import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { cartAPI, orderAPI } from "../services/api";
import "./RetailerCart.css";

const RetailerCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [retailer, setRetailer] = useState(null);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // ‚úÖ Fetch Cart
  const fetchCart = useCallback(async (retailerId) => {
    try {
      const res = await cartAPI.getCart(retailerId);
      console.log("üßæ Cart API Response:", res.data);

      const data = Array.isArray(res.data)
        ? res.data
        : res.data?.cartItems || res.data?.data || [];

      setCartItems(data);
      calculateTotal(data);
    } catch (err) {
      console.error("‚ùå Error fetching cart:", err);
      setCartItems([]);
    } finally {
      setLoading(false);
    }
  }, []);

  // ‚úÖ Calculate Total
  const calculateTotal = (items) => {
    if (!Array.isArray(items)) return setTotal(0);
    const totalAmount = items.reduce(
      (sum, item) => sum + (item.product?.price || 0) * (item.quantity || 0),
      0
    );
    setTotal(totalAmount);
  };

  // ‚úÖ Load on Mount
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      alert("Please log in to view your cart.");
      window.location.href = "/retailer/login";
      return;
    }
    setRetailer(storedRetailer);
    fetchCart(storedRetailer.id);
  }, [fetchCart]);

  // ‚úÖ Add or Increase Quantity
  const handleIncrease = async (productId) => {
    try {
      await cartAPI.addToCart(retailer.id, productId, 1);
      fetchCart(retailer.id);
    } catch (err) {
      console.error("Error increasing quantity:", err);
      alert("‚ùå Failed to update quantity.");
    }
  };

  // ‚úÖ Decrease Quantity (if quantity > 1)
  const handleDecrease = async (item) => {
    try {
      if (item.quantity <= 1) {
        await cartAPI.removeItem(item.id);
      } else {
        await cartAPI.updateQuantity(item.id, { quantity: item.quantity - 1 });
      }
      fetchCart(retailer.id);
    } catch (err) {
      console.error("Error decreasing quantity:", err);
      alert("‚ùå Failed to update quantity.");
    }
  };

  // ‚úÖ Remove Item
  const handleRemove = async (itemId) => {
    if (!window.confirm("üóëÔ∏è Remove this item from your cart?")) return;
    try {
      await cartAPI.removeItem(itemId);
      const updated = cartItems.filter((i) => i.id !== itemId);
      setCartItems(updated);
      calculateTotal(updated);
    } catch (err) {
      console.error("Error removing item:", err);
      alert("‚ùå Failed to remove item.");
    }
  };

  // ‚úÖ Clear Cart
  const handleClearCart = async () => {
    if (!retailer) return;
    if (!window.confirm("‚ö†Ô∏è Are you sure you want to clear your entire cart?")) return;

    try {
      await cartAPI.clearCart(retailer.id);
      setCartItems([]);
      setTotal(0);
      alert("üßπ Cart cleared successfully!");
    } catch (err) {
      console.error("Error clearing cart:", err);
      alert("‚ùå Failed to clear cart.");
    }
  };

  // ‚úÖ Place Full Order
  const handlePlaceOrder = async () => {
    if (!window.confirm("‚úÖ Confirm placing your full order?")) return;
    try {
      const res = await orderAPI.placeOrder(retailer.id);
      alert("‚úÖ Order placed! Redirecting to payment...");
      setCartItems([]);
      setTotal(0);
      navigate("/payment", {
        state: {
          order: { id: res?.data?.id || retailer.id, totalAmount: total },
        },
      });
    } catch (err) {
      console.error("Error placing order:", err);
      alert("‚ùå Failed to place order. Try again.");
    }
  };

  if (loading) return <p className="loading">Loading your cart...</p>;

  return (
    <div className="cart-page">
      <div className="cart-header">
        <h1>üõí Your Shopping Cart</h1>
        {retailer && (
          <p>
            Welcome, <b>{retailer.storeName || retailer.name}</b>
          </p>
        )}
      </div>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <h2>üõçÔ∏è Your cart is empty</h2>
          <p>Browse products and add them to your cart to continue shopping.</p>
          <a href="/retailer/dashboard" className="back-btn">
            ‚Üê Back to Dashboard
          </a>
        </div>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Farmer</th>
                <th>Quantity</th>
                <th>Price (each)</th>
                <th>Total</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>{item.product?.name || "N/A"}</td>
                  <td>üë®‚Äçüåæ {item.product?.farmer?.name || "Unknown"}</td>
                  <td>
                    <div className="qty-controls">
                      <button onClick={() => handleDecrease(item)} className="qty-btn">
                        ‚ûñ
                      </button>
                      <span className="qty-value">{item.quantity}</span>
                      <button
                        onClick={() => handleIncrease(item.product.id)}
                        className="qty-btn"
                      >
                        ‚ûï
                      </button>
                    </div>
                  </td>
                  <td>‚Çπ{item.product?.price?.toFixed(2) || 0}</td>
                  <td>
                    ‚Çπ{((item.product?.price || 0) * (item.quantity || 0)).toFixed(2)}
                  </td>
                  <td>
                    <div className="cart-actions">
                      <button
                        onClick={() => handleRemove(item.id)}
                        className="remove-btn"
                      >
                        ‚ùå Remove
                      </button>
                      <button
                        onClick={() => handleBuyNow(item)}
                        className="buy-btn"
                      >
                        üí∞ Buy
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="cart-summary">
            <div className="summary-details">
              <h3>Total Amount:</h3>
              <h2 className="price-tag">‚Çπ{total.toFixed(2)}</h2>
            </div>
            <div className="summary-actions">
              <button onClick={handlePlaceOrder} className="place-btn">
                ‚úÖ Place Order
              </button>
              <button onClick={handleClearCart} className="clear-btn">
                üßπ Clear Cart
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default RetailerCart;
