import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { authAPI } from "../services/api";

const FarmerLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault(); // ‚úÖ Prevent default form reload
    setLoading(true);
    setError("");

    try {
      // ‚úÖ Call backend login API
      const res = await authAPI.farmerLogin({ email, password });

      // ‚úÖ Backend should return farmer data
      const farmer = res.data;

      if (!farmer || !farmer.id) {
        throw new Error("Invalid login response. Please check backend.");
      }

      // ‚úÖ Store farmer with credentials for Axios Basic Auth
      localStorage.setItem(
        "farmer",
        JSON.stringify({ ...farmer, email, password })
      );

      console.log("‚úÖ Farmer logged in:", farmer);

      // ‚úÖ Navigate to dashboard
      navigate("/farmer/dashboard", { replace: true });
      window.location.reload(); // refresh context
    } catch (err) {
      console.error("‚ùå Login failed:", err);
      const errMsg =
        err.response?.data?.error ||
        err.response?.data?.message ||
        "Invalid email or password";
      setError(errMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {/* üîπ Navigation Bar */}
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">
            Login
          </Link>
          <Link to="/farmer/signup" className="nav-link">
            FARMER SIGNUP
          </Link>
        </div>
      </nav>

      {/* üîπ Login Form */}
      <div className="form-container">
        <h2>Farmer Login</h2>
        {error && <div className="alert alert-error">‚ùå {error}</div>}

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>

          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        <p style={{ textAlign: "center", marginTop: "1rem" }}>
          Don‚Äôt have an account?{" "}
          <Link to="/farmer/signup" style={{ fontWeight: "bold" }}>
            Sign up here
          </Link>
        </p>
      </div>
    </div>
  );
};

export default FarmerLogin;
