import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const fetchProducts = async () => {
    setLoading(true);
    setError("");
    try {
      const farmer = JSON.parse(localStorage.getItem("user"));
      if (!farmer) {
        alert("Please log in as a farmer!");
        setLoading(false);
        return;
      }
      const response = await productAPI.getMyProducts(farmer.email);
      setProducts(response.data);
    } catch (err) {
      const msg = err.response?.data?.message || "Failed to load products.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [refreshKey]);

  const getImageUrl = (imagePath) => (imagePath ? `http://localhost:8080/uploads/${imagePath}` : null);
  const getQrUrl = (qrPath) => (qrPath ? `http://localhost:8080${qrPath}` : null); // qrPath like "/qr/QR_20.png"

  if (loading) return <div>Loading your products...</div>;
  if (error) return <div style={{ color: "red" }}>‚ùå {error}</div>;
  if (!products || products.length === 0) {
    return (
      <div className="no-products">
        <h3>No products found</h3>
        <p>Add your first product!</p>
      </div>
    );
  }

  return (
    <div className="product-list-container">
      <h2>üß∫ My Products</h2>
      <div className="products-grid">
        {products.map((product) => {
          const imageUrl = getImageUrl(product.imagePath);
          const qrUrl = getQrUrl(product.qrCodePath);

          return (
            <div key={product.id} className="product-card">
              <div className="product-image-container">
                {imageUrl ? <img src={imageUrl} alt={product.name} className="product-image" /> : <div className="image-placeholder">No Photo</div>}
              </div>

              <div className="product-qr-container">
                {qrUrl ? <img src={qrUrl} alt="QR Code" className="qr-image" /> : <div className="qr-placeholder">No QR</div>}
              </div>

              <div className="product-info">
                <h3>{product.name}</h3>
                <p>üì¶ {product.category}</p>
                <p>üí∞ ‚Çπ{product.price}</p>
                <p>üî¢ Batch: {product.batchNumber}</p>
                <p>üóìÔ∏è {product.productionDate ? new Date(product.productionDate).toLocaleDateString() : "N/A"}</p>
                {product.description && <p>üìù {product.description}</p>}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
